import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import AppLayout from '../components/AppLayout';
import PostForm from '../components/PostForm';
import PostCard from '../components/PostCard';
import { LOAD_POSTS_REQUEST } from '../reducers/post';

const Home = () => {
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch({
            type: LOAD_POSTS_REQUEST,
        });
    }, []);

    // 스크롤 내려서 끝까지 갔을 때 로딩하게 하기
    useEffect(() => {
        function onScroll() {
            console.log(window.screenY, document.documentElement.clientHeight, document.documentElement.scrollHeight);
        }
        // useEffect에서 window.addEventListener선언할 땐 꼭 리턴해서 리무브 해주기 안그러면 메모리에 쌓임
        window.addEventListener('scroll', onScroll);
        return () => {
            window.removeEventListener('acroll', onScroll);
        };
    }, []);

    const { me } = useSelector((state) => state.user);
    const { mainPosts } = useSelector((state) => state.post);
    return (
        <AppLayout>
            {me && <PostForm />}
            {mainPosts.map((post) => <PostCard key={post.id} post={post} />)}
        </AppLayout>
    );
};

export default Home;
