import { HYDRATE } from 'next-redux-wrapper';
import user from './user';
import post from './post';

// 스테이트를 바꾸고 싶을 때
const initialState = {
    user: {
        
    },
    post: {
        
    }
};

// state에 들어간것은 기본state
// (이전상태, 액션) => 다음상태를 만들어냄
const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case HYDRATE :
            console.log(HYDRATE);
            return {...state, ...action.payload};
        // 기본값이 없으면 에러남
        default :
            return state;
    }
};

export default rootReducer;
